mapper.sql:
FROM infrCode a
INNER JOIN infrCodeGroup b on a.infrCodeGroup_ifcgSeq = b.ifcgSeq
WHERE 1=1
	AND ifcgDelNy = #{shDelNy}
	AND ifcgUseNy = #{shUseNy}

		AND ifcdSeq = #{shValue}
		AND ifcdName LIKE CONCAT('%',#{shValue},'%')
		AND ifcdEngName LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO infrCode (
	ifcdName
	,ifcdEngName
	,ifcdUseNy
	,ifcdOrder
	,infrCodeGroup_ifcgSeq
)
VALUES (
	#{ifcdName}
	,#{ifcdEngName}
	,1
	,#{ifcdOrder}
	,#{infrCodeGroup_ifcgSeq}
)

	SELECT last_insert_id()

mapper.update:
UPDATE infrCode
SET
	ifcdName = #{ifcdName}
	,ifcdEngName = #{ifcdEngName}

WHERE 
	ifcdSeq = #{ifcdSeq}
