mapper.select:
SELECT a.* ,(select count(ifcdName) from infrCode aa where 1=1 and aa.infrCodeGroup_ifcgSeq = a.ifcgSeq) as count FROM infrCodeGroup a WHERE 1=1
AND ifcgUseNy = #{shUseNy}
AND ifcgSeq = #{shValue} AND ifcgName LIKE CONCAT('%',#{shValue},'%') AND ifcgEngName LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO infrCodeGroup (
	ifcgName
    ,ifcgEngName
    ,ifcgUseNy
    ,ifcgOrder
    ,ifcgRegDate
	)
VALUES (
	#{ifcgName}
    ,#{ifcgEngName}
    ,1
    ,#{ifcgOrder}
    ,#{ifcgRegDate}
)

	SELECT last_insert_id()

mapper.update:
UPDATE infrCodeGroup
SET
	ifcgName = #{ifcgName}
	,ifcgEngName = #{ifcgEngName}

WHERE 
	ifcgSeq = #{ifcgSeq}
