mapper.update:
UPDATE infrMember
SET
	ifmmName = #{ifmmName}
	,ifmmGender = #{ifmmGender}
	,ifmmDob = #{ifmmDob}
	,ifmmEmail = #{ifmmEmail}
	,ifmmEmailDomain = #{ifmmEmailDomain}
	,ifmmPhoneCarrier = #{ifmmPhoneCarrier}
	,ifmmPhone = #{ifmmPhone}
	,ifmmAddress = #{ifmmAddress}
	,ifmmPostNum = #{ifmmPostNum}
	,ifmmResidence = #{ifmmResidence}
	,ifmmDetailedAddress = #{ifmmDetailedAddress}
	,ifmmReference = #{ifmmReference}
	,ifmmSmsNy = #{ifmmSmsNy}
	,ifmmMailNy = #{ifmmMailNy}
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}

UPDATE infrMember
SET
	ifmmPassword = #{ifmmPassword}
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}

UPDATE infrMemberUploaded
SET
	type = #{type}
	,defaultNy = #{defaultNy}
	,sort = #{sort}
	,path = #{path}
	,originalName = #{originalName}
	,uuidName = #{uuidName}
	,ext = #{ext}
	,size = #{size}
WHERE 1=1
	AND delNy = 0
	AND pseq = #{ifmmSeq}

mapper.select:
SELECT
	COUNT(*)
FROM sdFavorite
WHERE 1=1
	AND infrMember_ifmmSeq = #{mainKey}
	AND sdWord_sdwSeq = #{sdWord_sdwSeq}
