mapper.sql:
FROM infrMember a
WHERE 1=1
	AND a.ifmmDelNy = #{shDelNy}

		AND a.ifmmSeq = #{shValue}
		AND a.ifmmName LIKE CONCAT('%',#{shValue},'%')
		AND a.ifmmId LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO infrMember (
	ifmmName
	,ifmmId
	,ifmmPassword
	)
VALUES(
	#{ifmmName}
	,#{ifmmId}
	,#{ifmmPassword}
)

	SELECT last_insert_id()

mapper.update:
UPDATE infrMember
SET
	ifmmName = #{ifmmName}
	,ifmmId = #{ifmmId}

WHERE 
	ifmmSeq = #{ifmmSeq}

mapper.delete:
DELETE FROM infrMember
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}

mapper.update:
UPDATE infrMemberUploaded
SET
	type = #{type}
	,defaultNy = #{defaultNy}
	,sort = #{sort}
	,path = #{path}
	,originalName = #{originalName}
	,uuidName = #{uuidName}
	,ext = #{ext}
	,size = #{size}
WHERE 1=1
	AND delNy = 0
	AND pseq = #{ifmmSeq}
