mapper.sql:
FROM infrMember a
WHERE 1=1
	AND ifmmDelNy = #{shDelNy}

		AND ifmmSeq = #{shValue}
		AND ifmmName LIKE CONCAT('%',#{shValue},'%')
		AND ifmmNickName LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO infrMember (
	ifmmName
	,ifmmNickName
	)
VALUES (
	#{ifmmName}
	,#{ifmmNickName}
)

	SELECT last_insert_id()

mapper.update:
UPDATE infrMember
SET
	ifmmName = #{ifmmName}
	,ifmmNickName = #{ifmmNickName}

WHERE 
	ifmmSeq = #{ifmmSeq}
